import React from 'react';
import './App.css';
import axios from 'axios';

import UserCard from './components/UserCard';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      userId: 'wsu718',
      gitHubUserContent: [],
      gitHubFollowers: []
    }
  };

  componentDidMount(){
    axios.get(`https://api.github.com/users/${this.state.userId}`)
    .then(response => {
      this.setState({
        gitHubUserContent: response.data
      })
    })
    .catch(error => {
      console.log('Error:', error.message);
    })

  }

  render() {
    return (
      <div className="App">
        <UserCard gitHubUserContent={this.gitHubUserContent}/>
      </div>
    )  
  }
};

export default App;

